const weatherCard=document.querySelector(".weather-card"),cityInput=document.querySelector(".city-input"),findButton=document.querySelector(".find-weather");findButton.addEventListener("click",()=>{let e=cityInput.value;fetch(`https://api.openweathermap.org/data/2.5/weather?q=${e}&appid=b417113fbb3f4e1a4a005dcce4bd1929`).then(e=>{if(e.ok)return e.json();{let e=document.querySelector(".error");e.classList.add("error-active"),setTimeout(()=>{e.classList.remove("error-active")},1500)}}).then(e=>{weatherCard.innerHTML=`<h2 class="weather-card__city">${e.name}</h2>\n                            <span class="weather-card__temperature">${Math.round(e.main.temp-273)} &deg;</span>\n                            <span class="weather-card__weather">${e.weather[0].main}</span>\n                            <img src="https://openweathermap.org/img/wn/${e.weather[0].icon}@4x.png" class="weather-card__weather-image" alt="Weather image" />\n                            <div class="wind">\n                                <h2 class="wind__title">Shamol</h2>\n                                <div class="wind__details">\n                                    <span class="wind__deg">${e.wind.deg}</span>\n                                    <span class="wind__speed">${e.wind.speed} m/s</span>\n                                </div>\n                            </div>`})});